import React, {Component} from 'react';

class AddButton extends Component {
    constructor(){
        super()
            this.state = {
                boxes: [],
        }
    }

    insertBox = () => {
        let boxesNew = this.state.boxes
        boxesNew.push('<Box />');
        this.setState({ boxes: boxesNew })
    }

    removeBox = () => {
        let boxesNew = this.state.boxes
        boxesNew.unshift();
        this.setState({ boxes: boxesNew })
    }



    render(){
        return (
            <div>
            <button onClick={ this.insertBox }>Add New Box</button>
            <button onClick={ this.removeBox }>Remove Box</button>
            <p>{this.state.boxes}</p>
            </div>
    )}
}

export default AddButton;
