import React, {Component} from 'react';
import Box from './Box'

class AddButton extends Component {
    constructor(){
        super()
            this.state = {
                boxes: [],
        }
    }

    insertBox = () => {
        let boxesNew = this.state.boxes
        boxesNew.push(<Box />);
        this.setState({ boxes: boxesNew })
    }

    removeBox = () => {
        let boxesNew = this.state.boxes
        boxesNew.pop();
        this.setState({ boxes: boxesNew })
    }



    render(){
        return (
            <div>
            <h1>
            <button onClick={ this.insertBox } className="addButtons">Add New Box</button>
            <button onClick={ this.removeBox } className="removeButtons app">Remove Box</button></h1>
            <p className= "App-Top">{this.state.boxes}</p>
            </div>
    )}
}

export default AddButton;
